sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
	initialize: function () {
	},

	isDatePast: function (strDate) {
			// rightnow stores the current time
			var rightnow = new GlideDateTime();
			// Create a GlideDateTime object for the When needed date
			var whenNeeded = new GlideDateTime(current.u_when_needed);
			// return boolean value for whenNeeded is before right now
			return whenNeeded.before(rightnow);
	},
	isDateToday: function (strDate) {
		// rightnow stores the current time
		var rightnow = new GlideDateTime();
		// Create a GlideDateTime object for the When needed date
		var whenNeeded = new GlideDateTime(current.u_when_needed);
		// today stores current date
		var today = rightnow.getLocalDate();
		// isToday gets date of whenNeeded
		var isToday = whenNeeded.getLocalDate();
		// if whenNeeded is today do not save, display error message
		return today.compareTo(isToday) == 0;
	},

	type: 'NeedItUtils'
};
